import React, { useState } from 'react';
import * as api from '../services/ApiServices';

function AddMedicine() {
  const [name, setName] = useState('');
  const [quantity, setQuantity] = useState('');
  const [expiryDate, setExpiryDate] = useState('');
  const [price, setPrice] = useState('');

  const addMedicineHandler = async (e) => {
    e.preventDefault();
    try {
      await api.createMedicine({ name, quantity, expiryDate, price });
      alert('Medicine added successfully');
    } catch (error) {
      console.error(error);
      alert('Error adding medicine');
    }
  };

  return (
    <div>
      <h1>Add Medicine</h1>
      <form onSubmit={addMedicineHandler}>
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
        <input type="number" placeholder="Quantity" value={quantity} onChange={(e) => setQuantity(e.target.value)} required />
        <input type="date" placeholder="Expiry Date" value={expiryDate} onChange={(e) => setExpiryDate(e.target.value)} required />
        <input type="number" placeholder="Price" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <button type="submit">Add Medicine</button>
      </form>
    </div>
  );
}

export default AddMedicine;
