import React, { useEffect, useState } from 'react';
import * as api from '../services/ApiServices';
import backgroundImage from '../assets/images/medicine_list_background.jpg'; // Ensure you have this image in your assets

function MedicineList() {
  const [medicines, setMedicines] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    const fetchMedicines = async () => {
      try {
        const { data } = await api.fetchMedicines();
        setMedicines(data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchMedicines();
  }, []);

  const filteredMedicines = medicines.filter(medicine =>
    medicine.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={styles.container}>
      <div style={styles.listContainer}>
        <h1 style={styles.title}>Medicines</h1>
        <input
          type="text"
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={styles.input}
        />
        <ul style={styles.list}>
          {filteredMedicines.map((medicine) => (
            <li key={medicine._id} style={styles.listItem}>
              {medicine.name} - {medicine.quantity} - {medicine.expiryDate} - {medicine.price}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
  },
  listContainer: {
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    maxWidth: '600px',
    width: '100%',
    textAlign: 'center',
  },
  title: {
    marginBottom: '20px',
    color: '#333',
  },
  input: {
    marginBottom: '20px',
    padding: '10px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '100%',
  },
  list: {
    listStyleType: 'none',
    padding: 0,
  },
  listItem: {
    padding: '10px',
    borderBottom: '1px solid #ccc',
    marginBottom: '10px',
  },
};

export default MedicineList;
